    });
  };

  tinhTongSoLuong = () => {
      let tongSL = 0;
      for (let spGH of this.props.gioHang){
          tongSL += spGH.soLuong;
      }
      return tongSL;
  }


  render() {
    return (
      <div className="container">
        <h2 className="text-danger">Your Cart <i className="fa fa-shopping-cart"></i> ({this.tinhTongSoLuong()}) </h2>
